import React, { useState } from 'react';

function RouteFinder() {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [routeData, setRouteData] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();

    const fromEncoded = encodeURIComponent(from);
    const toEncoded = encodeURIComponent(to);
    const url = `/route?from=${fromEncoded}&to=${toEncoded}`;

    fetch(url)
      .then(response => response.json())
      .then(data => setRouteData(data))
      .catch(error => console.error(error));
  };

  return (
    <div>
      <h1>Delhi Metro Route Finder</h1>

      <form onSubmit={handleSubmit}>
        <label htmlFor="from">From:</label>
        <input type="text" id="from" name="from" value={from} onChange={(event) => setFrom(event.target.value)} required />
        <br />
        <label htmlFor="to">To:</label>
        <input type="text" id="to" name="to" value={to} onChange={(event) => setTo(event.target.value)} required />
        <br />
        <button type="submit">Find Route</button>
      </form>

      {routeData && (
        <pre>{JSON.stringify(routeData, null, 2)}</pre>
      )}
    </div>
  );
}

export default RouteFinder;
